
public class AutoExplicit {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int A = 30;
		double B = A;
		float C = (float)30.0;
		int D = (int)30;

		System.out.println("The Integer number is : " + A);
		System.out.println("The Double number is : " + B);
		System.out.println("The Float number is : " + C);
		System.out.println("The Integer number is : " + D);
	}
		
}
